contracts/BridgeToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title BridgeToken
 * @dev ERC20 токен с возможностью выпуска и сжигания токенов
 * Только доверенные контракты (роль MINTER_BURNER_ROLE) могут выпускать и сжигать токены
 */
contract BridgeToken is ERC20, AccessControl {
    // Роль для выпуска и сжигания токенов
    bytes32 public constant MINTER_BURNER_ROLE = keccak256("MINTER_BURNER_ROLE");

    // Максимальное количество токенов, которое может быть выпущено
    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18; // 1 миллиард токенов

    // События для отслеживания операций
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount, string reason);

    constructor(
        string memory name,
        string memory symbol,
        address initialOwner
    ) ERC20(name, symbol) {
        // Устанавливаем владельца контракта
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);

        // Владелец также получает роль минтера/бёрнера
        _grantRole(MINTER_BURNER_ROLE, initialOwner);
    }

    /**
     * @dev Выпуск новых токенов
     * @param to Адрес получателя токенов
     * @param amount Количество токенов для выпуска
     * @param reason Причина выпуска токенов (для логирования)
     */
    function mint(address to, uint256 amount, string calldata reason) external onlyRole(MINTER_BURNER_ROLE) {
        require(to != address(0), "BridgeToken: mint to the zero address");
        require(amount > 0, "BridgeToken: amount must be greater than 0");
        require(totalSupply() + amount <= MAX_SUPPLY, "BridgeToken: max supply exceeded");

        _mint(to, amount);
        emit TokensMinted(to, amount, reason);
    }

    /**
     * @dev Сжигание токенов
     * @param from Адрес, с которого сжигаются токены
     * @param amount Количество токенов для сжигания
     * @param reason Причина сжигания токенов (для логирования)
     */
    function burn(address from, uint256 amount, string calldata reason) external onlyRole(MINTER_BURNER_ROLE) {
        require(from != address(0), "BridgeToken: burn from the zero address");
        require(amount > 0, "BridgeToken: amount must be greater than 0");
        require(balanceOf(from) >= amount, "BridgeToken: burn amount exceeds balance");

        _burn(from, amount);
        emit TokensBurned(from, amount, reason);
    }

    /**
     * @dev Сжигание токенов с проверкой разрешения
     * @param amount Количество токенов для сжигания
     * @param reason Причина сжигания токенов (для логирования)
     */
    function burnFrom(address from, uint256 amount, string calldata reason) external onlyRole(MINTER_BURNER_ROLE) {
        require(from != address(0), "BridgeToken: burn from the zero address");
        require(amount > 0, "BridgeToken: amount must be greater than 0");

        uint256 currentAllowance = allowance(from, msg.sender);
        require(currentAllowance >= amount, "BridgeToken: burn amount exceeds allowance");

        _approve(from, msg.sender, currentAllowance - amount);
        _burn(from, amount);
        emit TokensBurned(from, amount, reason);
    }

    /**
     * @dev Добавление нового доверенного контракта
     * @param account Адрес контракта для добавления
     */
    function addTrustedContract(address account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(account != address(0), "BridgeToken: account is the zero address");
        _grantRole(MINTER_BURNER_ROLE, account);
    }

    /**
     * @dev Удаление доверенного контракта
     * @param account Адрес контракта для удаления
     */
    function removeTrustedContract(address account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(account != address(0), "BridgeToken: account is the zero address");
        _revokeRole(MINTER_BURNER_ROLE, account);
    }

    /**
     * @dev Проверка, является ли адрес доверенным контрактом
     * @param account Адрес для проверки
     * @return true, если адрес имеет роль MINTER_BURNER_ROLE
     */
    function isTrustedContract(address account) external view returns (bool) {
        return hasRole(MINTER_BURNER_ROLE, account);
    }
}

contracts/TokenBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./BridgeToken.sol";

/**
 * @title TokenBridge
 * @dev Контракт моста для перемещения токенов между сетями
 * Обеспечивает сжигание токенов в одной сети и выпуск в другой
 */
contract TokenBridge is AccessControl, ReentrancyGuard, Pausable {
    // Роль для операторов моста
    bytes32 public constant BRIDGE_OPERATOR_ROLE = keccak256("BRIDGE_OPERATOR_ROLE");

    // Структура для хранения информации о переводе
    struct Transfer {
        address token;
        address from;
        address to;
        uint256 amount;
        uint256 timestamp;
        bool processed;
        string reason;
    }

    // Маппинг для отслеживания обработанных переводов
    mapping(bytes32 => bool) public processedTransfers;

    // Маппинг для хранения детальной информации о переводах
    mapping(bytes32 => Transfer) public transferDetails;

    // Счетчик для генерации уникальных ID переводов
    uint256 public transferCounter;

    // События
    event TokensLocked(
        bytes32 indexed transferId,
        address indexed token,
        address indexed from,
        address to,
        uint256 amount,
        string reason
    );

    event TokensUnlocked(
        bytes32 indexed transferId,
        address indexed token,
        address indexed to,
        uint256 amount,
        string reason
    );

    event TransferProcessed(
        bytes32 indexed transferId,
        bool success,
        string reason
    );

    event BridgeOperatorAdded(address indexed operator);
    event BridgeOperatorRemoved(address indexed operator);

    constructor(address initialOwner) {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(BRIDGE_OPERATOR_ROLE, initialOwner);
    }

    /**
     * @dev Блокировка токенов для перевода в другую сеть
     * @param token Адрес токена для блокировки
     * @param to Адрес получателя в целевой сети
     * @param amount Количество токенов для блокировки
     * @param reason Причина перевода
     * @return transferId Уникальный ID перевода
     */
    function lockTokens(
        address token,
        address to,
        uint256 amount,
        string calldata reason
    ) external whenNotPaused nonReentrant returns (bytes32) {
        require(token != address(0), "TokenBridge: token address cannot be zero");
        require(to != address(0), "TokenBridge: recipient address cannot be zero");
        require(amount > 0, "TokenBridge: amount must be greater than zero");

        // Создаем уникальный ID перевода
        bytes32 transferId = keccak256(
            abi.encodePacked(
                block.chainid,
                transferCounter++,
                msg.sender,
                token,
                to,
                amount,
                block.timestamp
            )
        );

        // Проверяем, что перевод еще не был обработан
        require(!processedTransfers[transferId], "TokenBridge: transfer already processed");

        // Получаем экземпляр токена
        BridgeToken tokenContract = BridgeToken(token);

        // Проверяем баланс отправителя
        require(tokenContract.balanceOf(msg.sender) >= amount, "TokenBridge: insufficient balance");

        // Переводим токены на контракт моста
        require(tokenContract.transferFrom(msg.sender, address(this), amount), "TokenBridge: transfer failed");

        // Сжигаем токены
        tokenContract.burn(address(this), amount, string(abi.encodePacked("Bridge lock: ", reason)));

        // Сохраняем информацию о переводе
        transferDetails[transferId] = Transfer({
            token: token,
            from: msg.sender,
            to: to,
            amount: amount,
            timestamp: block.timestamp,
            processed: true,
            reason: reason
        });

        processedTransfers[transferId] = true;

        emit TokensLocked(transferId, token, msg.sender, to, amount, reason);

        return transferId;
    }

    /**
     * @dev Разблокировка токенов в целевой сети
     * @param transferId ID перевода из исходной сети
     * @param token Адрес токена для разблокировки
     * @param to Адрес получателя
     * @param amount Количество токенов для разблокировки
     * @param reason Причина разблокировки
     */
    function unlockTokens(
        bytes32 transferId,
        address token,
        address to,
        uint256 amount,
        string calldata reason
    ) external onlyRole(BRIDGE_OPERATOR_ROLE) whenNotPaused nonReentrant {
        require(token != address(0), "TokenBridge: token address cannot be zero");
        require(to != address(0), "TokenBridge: recipient address cannot be zero");
        require(amount > 0, "TokenBridge: amount must be greater than zero");
        require(!processedTransfers[transferId], "TokenBridge: transfer already processed");

        // Получаем экземпляр токена
        BridgeToken tokenContract = BridgeToken(token);

        // Выпускаем новые токены
        tokenContract.mint(to, amount, string(abi.encodePacked("Bridge unlock: ", reason)));

        // Сохраняем информацию о переводе
        transferDetails[transferId] = Transfer({
            token: token,
            from: address(0), // В исходной сети
            to: to,
            amount: amount,
            timestamp: block.timestamp,
            processed: true,
            reason: reason
        });

        processedTransfers[transferId] = true;

        emit TokensUnlocked(transferId, token, to, amount, reason);
        emit TransferProcessed(transferId, true, "Tokens unlocked successfully");
    }

    /**
     * @dev Проверка статуса перевода
     * @param transferId ID перевода
     * @return processed true, если перевод был обработан
     * @return transfer Детали перевода
     */
    function getTransferStatus(bytes32 transferId) external view returns (bool processed, Transfer memory transfer) {
        processed = processedTransfers[transferId];
        transfer = transferDetails[transferId];
    }

    /**
     * @dev Добавление оператора моста
     * @param operator Адрес оператора
     */
    function addBridgeOperator(address operator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(operator != address(0), "TokenBridge: operator address cannot be zero");
        _grantRole(BRIDGE_OPERATOR_ROLE, operator);
        emit BridgeOperatorAdded(operator);
    }

    /**
     * @dev Удаление оператора моста
     * @param operator Адрес оператора
     */
    function removeBridgeOperator(address operator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(operator != address(0), "TokenBridge: operator address cannot be zero");
        _revokeRole(BRIDGE_OPERATOR_ROLE, operator);
        emit BridgeOperatorRemoved(operator);
    }

    /**
     * @dev Приостановка работы моста
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Возобновление работы моста
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Проверка, является ли адрес оператором моста
     * @param account Адрес для проверки
     * @return true, если адрес имеет роль BRIDGE_OPERATOR_ROLE
     */
    function isBridgeOperator(address account) external view returns (bool) {
        return hasRole(BRIDGE_OPERATOR_ROLE, account);
    }

    /**
     * @dev Получение следующего счетчика переводов
     * @return Следующий ID перевода
     */
    function getNextTransferId() external view returns (uint256) {
        return transferCounter;
    }
}

